mapper.update:
UPDATE ecb_account

    yn = 0

WHERE as_id = #{asId}
  AND user_id = #{userId}
  AND yn = 1

mapper.select:
select as_id, app_name, user_name, '******', remark
  from ecb_account
 where user_id = #{ecbAccount.userId}

    and app_name like #{ecbAccount.appName}

order by update_time desc
limit #{page.pageStart}, #{page.pageSize}

select count(*)
  from ecb_account
 where user_id = #{userId}

    and app_name like #{appName}
